function extractCodeBlocks(text, blockTypes) {
  const results = [];
  const allBlocks = !blockTypes || blockTypes.length === 0;

  let codeBlockRegex;
  if (allBlocks) {
    codeBlockRegex = /```(\w*)\n([\s\S]*?)\n```/g;
  } else {
    const regexStr = blockTypes.map((type) => type).join('|');
    codeBlockRegex = new RegExp(`\`\`\`(${regexStr})\n([\\s\\S]*?)\\n\`\`\``, 'g');
    // const jsonBlockRegex = new RegExp(`\`\`\`((json))\n([\\s\\S]*?)\`\`\``, 'g');
    // console.log('manual match?', jsonBlockRegex.exec(text))
  }

  let match;
  while ((match = codeBlockRegex.exec(text)) !== null) {
    // console.log('got match', match);
    const type = match[1];
    const content = match[2];
    results.push({ type, content });
  }

  return results;
}

