function extractCodeBlocks(text, blockTypes) {
  const results = [];
  const allBlocks = !blockTypes || blockTypes.length === 0;

  let codeBlockRegex;
  if (allBlocks) {
    codeBlockRegex = /```(\w*)\n([\s\S]*?)\n```/g;
  } else {
    const regexStr = blockTypes.map((type) => type).join('|');
    codeBlockRegex = new RegExp(`\`\`\`(${regexStr})\n([\\s\\S]*?)\\n\`\`\``, 'g');
